  render(){
    return (
    <div className="controls" style = {{height:"100%",width:"100%"}}>
    {this.state.vis ? this.stopMicrophone() : this.getMicrophone()}
    {this.state.vis ? <AudioAnalyser audio={this.state.audio} /> : ''}</div>);
  }

}

export default Index;

